import React, {useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'
import './signup.css'

const Signup = (props)=> {

    const [temp,setTemp]=useState({
                    Name:"",
                    Email:"",
                    Password:""

    });
    const [error,setError]=useState({
                    NameErr:{},
                    EmailErr:{},
                    PasswordErr:{},
    });
    const add =()=>{
        debugger;
        let isValid = FormValidation();
        if(isValid === true){
            const register = {
            name: temp.Name,
            email: temp.Email,
            password: temp.Password
        }
        axios.post("http://localhost:4000/app/signup", register)
        .then(response => {
            console.log(response.data)
            if(response.data.message === "User Added Successfully!"){
                alert("User Added Successfully!")
            }
            else{
                alert("Invalid Name,Email ID or Password!")
            }
        })
    }

    }


    const handleChange =(e)=>{
        
        let {name, value}=e.target;
        setTemp((prev)=>({
            ...prev,
            [name]:value
        }))
    }
    const FormValidation = () =>{

            const NameErr={};
            const EmailErr={};
            const PasswordErr={};

            let isValid= true;

        if(temp.Name.length < 1 ){
            NameErr.nameSort="Enter Name";
            isValid =false;
        } 
        if(temp.Email.length < 1){
            EmailErr.EmailSort="Enter Email";
            isValid =false;
        }
        else if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(temp.Email)){
            EmailErr.EmailSort="Enter a valid Email";
            isValid =false;
        }
        if(temp.Password.length < 1 ){
            PasswordErr.PasswordSort="Enter Password";
            isValid =false;
        }
        else if(temp.Password.length < 6 ){
            PasswordErr.PasswordSort="Password must be minimum 6 characters";
            isValid =false;
        }
        setError({
            NameErr:NameErr,
            EmailErr:EmailErr,
            PasswordErr:PasswordErr,
        })
        return isValid;
    }

return (
    // <div >
    //     <div className="container py-4 h-40">
    //         <div className="row d-flex justify-content-center align-items-center h-40">
    //             <div className="col-12 col-md-8 col-lg-6 col-xl-5">
    //                 <div className="card bg-dark text-white" >
    //                     <div className="card-body p-4 text-center">
                            <div className="signupContainer">
                                <div className="nameContainer">
                                    <label className="nameLabel" for="Name">Name:</label>
                                    {Object.keys(error.NameErr).map((key) => {
                                        return (
                                        <span className="nameErr">{error.NameErr[key]}</span>
                                        );
                                    })} 
                                    <br/>
                                    <input className="nameInput" type="text" name="Name" placeholder="Name" id="Name" onChange={handleChange} value={temp.Name}></input>
                                    
                                    <br/>
                                </div>
                                <div className="emailContainer">
                                    <label className="emailLabel" for="Email">Email:</label>
                                    {Object.keys(error.EmailErr).map((key) => {
                                        return (
                                        <span className="emailErr">{error.EmailErr[key]}</span>
                                        );
                                    })} 
                                    <br/>
                                    <input className="emailInput" type="email" name="Email" placeholder="Email" id="Email" onChange={handleChange} value={temp.Email}></input>
                                    <br/>
                                </div>
                                <div className="passContainer">
                                    <label className="passLabel" for="Password">Password:</label>
                                    {Object.keys(error.PasswordErr).map((key) => {
                                        return (
                                        <span className="passErr">{error.PasswordErr[key]}</span>
                                        );
                                    })} 
                                    <br/>
                                    <input className="passInput" type="password" name="Password" placeholder="Password" id="Password" onChange={handleChange} value={temp.Password}></input>
                                    

                                    <br/>
                                </div>
                                <button className="signupBtn" onClick={add}>SIGNUP</button>
                                <Link to="/"><h3 className="navLogin">LOGIN</h3></Link>
                            </div>
    //     </div>
    //     </div>
    //     </div>
    //     </div>
    //     </div>
    // </div>
)
}

export default Signup;
