import React, {useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'


const Signup = (props)=> {

    const [temp,setTemp]=useState({
                    Name:"",
                    Email:"",
                    Password:""

    });
    const [error,setError]=useState({
                    NameErr:{},
                    EmailErr:{},
                    PasswordErr:{},
    });
    const add =()=>{
        debugger;
        let isValid = FormValidation();
        if(isValid === true){
            const register = {
            name: temp.Name,
            email: temp.Email,
            password: temp.Password
        }
        axios.post("http://localhost:4000/app/signup", register)
        .then(response => {
            console.log(response.data)
            if(response.data.message === "User Added Successfully!"){
                alert("User Added Successfully!")
            }
            else{
                alert("Invalid Name,Email ID or Password!")
            }
        })
        // props.history.push("/")
    }

    }


    const handleChange =(e)=>{
        
        let {name, value}=e.target;
        setTemp((prev)=>({
            ...prev,
            [name]:value
        }))
    }
    const FormValidation = () =>{

            const NameErr={};
            const EmailErr={};
            const PasswordErr={};

            let isValid= true;
    // try{
        if(temp.Name.length < 1 ){
            NameErr.nameSort="Enter Name";
            isValid =false;
        } 
        if(temp.Email.length < 1){
            EmailErr.EmailSort="Enter Email";
            isValid =false;
        }
        else if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(temp.Email)){
            EmailErr.EmailSort="Enter a valid Email";
            isValid =false;
        }
        if(temp.Password.length < 1 ){
            PasswordErr.PasswordSort="Enter Password";
            isValid =false;
        }
        else if(temp.Password.length < 6 ){
            PasswordErr.PasswordSort="Password must be minimum 6 characters";
            isValid =false;
        }
        setError({
            NameErr:NameErr,
            EmailErr:EmailErr,
            PasswordErr:PasswordErr,
        })
        return isValid;
    }

return (
    <div >
        {/* <h1> SIGNUP</h1> */}
        <br/>
        <div className="container py-4 h-40">
            <div className="row d-flex justify-content-center align-items-center h-40">
                <div className="col-12 col-md-8 col-lg-6 col-xl-5">
                    <div className="card bg-dark text-white" >
                        <div className="card-body p-4 text-center">
                            <div className="mb-md-5 mt-md-4 pb-4">
            <div className="form-outline mb-1">
                <label className="form-label" for="Name">Name:</label>
                <input className="form-control " type="text" name="Name" placeholder="Name" id="Name" onChange={handleChange} value={temp.Name}></input>
                
                {Object.keys(error.NameErr).map((key) => {
                    return (
                    <p className="small mb-1 pb-lg-1 text-warning">{error.NameErr[key]}</p>
                    );
                })} 
                <br/>
            </div>
            <div className="form-outline form-white mb-1">
                <label className="form-label" for="Email">Email:</label>
                <input className="form-control " type="email" name="Email" placeholder="Email" id="Email" onChange={handleChange} value={temp.Email}></input>
                
                {Object.keys(error.EmailErr).map((key) => {
                    return (
                    <p className="small mb-1 pb-lg-1 text-warning">{error.EmailErr[key]}</p>
                    );
                })} 
                <br/>
            </div>
            <div className="form-outline form-white mb-1">
                <label className="form-label" for="Password">Password:</label>
                <input className="form-control " type="password" name="Password" placeholder="Password" id="Password" onChange={handleChange} value={temp.Password}></input>
                
                {Object.keys(error.PasswordErr).map((key) => {
                    return (
                    <p className="small mb-1 pb-lg-1 text-warning">{error.PasswordErr[key]}</p>
                    );
                })} 
                <br/>
            </div>
            <button className="btn btn-outline-light btn-lg px-5" onClick={add}>SIGNUP</button>
            <Link to="/"><h1>Back</h1></Link>
            {/* <button onClick={clear}>clear</button> */}
            {/* <p>{temp.ID}{temp.Name} {temp.Mobile} {temp.Gender} {temp.Email} </p> */}
        </div>
        </div>
        </div>
        </div>
        </div>
        </div>
    </div>
)
}

export default Signup;
