import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'
import './login.css'


function Login(props) {
    debugger;

    const [loginProps, setLoginProps]=useState({
        Email:"",
        Password:"",
    });
        const [loginErrors, setLoginErrors]=useState({
        EmailErr:"",
        PassErr:"",

        invalidEmail:"",
        invalidPass:"",
        propsValid:"",
    })
    
    const LoginBtn =() =>{
        debugger;
        let isvalid =validation();

        if(isvalid === true){

        const loginParams = {
            email: loginProps.Email,
            password: loginProps.Password
        }

        axios.post("http://localhost:4000/app/login", loginParams)
        .then(response => {
            console.log(response.data.code)
            if(response.data.code === 0){
                alert(response.data.message)
            }
            else if(response.data.code === 1){
                alert(response.data.message)
            }
        })
        }
        else{
            alert(`something went wrong`)
        }
        
    }

    const validation =()=>{
        debugger;
        const EmailErr={};
        const PassErr={};
        let isValid= true;
    try{
        if(loginProps.Email.length < 1){
            EmailErr.emailsort="Enter Email";
            isValid =false;
        }
        else if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(loginProps.Email)){
            EmailErr.EmailSort="Enter a valid Email";
            isValid =false;
        }
        if(loginProps.Password.length < 1){
            PassErr.passsort="Enter Password";
            isValid =false;
        }
        else if(loginProps.Password.length < 6 ){
            PassErr.PasswordSort="Enter a valid Password";
            isValid =false;
        }
    setLoginErrors({
        EmailErr: EmailErr,
        PassErr: PassErr
    })

        return isValid;
    }
    catch(error){
    isValid =false
    }
    }

    const handleChange = (e)=>{
        let {name,value}=e.target;
        setLoginProps((prev)=>({
            ...prev,
            [name]:value
        }))
    }

    return (
    <section className="vh-100 gradient-custom">
        <div className="container py-4 h-40">
            <div className="row d-flex justify-content-center align-items-center h-40">
                <div className="col-12 col-md-8 col-lg-6 col-xl-5">
                    <div className="card bg-dark text-white" >
                        <div className="card-body p-4 text-center">
                            <div className="mb-md-5 mt-md-4 pb-4">

                                <div className="form-outline form-white mb-4">
                                    <label className="form-label" for="typeEmailX">Email</label>
                                    <input type="email" placeholder="Email" id="typeEmailX" name="Email" className="form-control form-control-lg" onChange={handleChange} value={loginProps.Email} />
                                    {Object.keys(loginErrors.EmailErr).map((key) => {
                                    return (
                                        <p className="small mb-1 pb-lg-1 text-warning">{loginErrors.EmailErr[key]}</p>
                                    );
                                    })} 
                                </div>

                                <div className="form-outline form-white mb-5">
                                    <label className="form-label" for="typePasswordX">Password</label>
                                    <input type="password" placeholder="Password" id="typePasswordX" name="Password" className="form-control form-control-lg" onChange={handleChange} value={loginProps.Password} />
                                    {Object.keys(loginErrors.PassErr).map((key) => {
                                    return (
                                        <p className="small mb-1 pb-lg-1 text-warning">{loginErrors.PassErr[key]}</p>
                                    );
                                    })} 
                                </div>

                                    

                                <button className="btn btn-outline-light btn-lg px-5" onClick={LoginBtn}>Login</button>

                                <Link to='/signup'><h3>Signup</h3></Link>

                            
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </section>
    )
}

export default Login
